"""2023-04-10"""
from helpers import HEIGHT
from helpers import save_image
from helpers import WIDTH
from helpers import write_legend
from py5 import *
from pathlib import Path


IMG_NAME = Path(__file__).name.replace(".py", "")


# Reference https://decimal.info/digits-of-pi/value-of-pi-to-2120-decimal-places.html
PI = "3."  # noQA

# Reference https://carbondesignsystem.com/data-visualization/color-palettes/
PALETTE = [
    "#f6f2ff",
    "#e8daff",
    "#d4bbff",
    "#be95ff",
    "#a56eff",
    "#8a3ffc",
    "#6929c4",
    "#491d8b",
    "#31135e",
    "#1c0f30",
]


def settings():
    size(WIDTH, HEIGHT)


def setup():
    fundo()
    write_text()
    pi_circle()
    write_legend(PALETTE, IMG_NAME)
    save_image(IMG_NAME)


def fundo():
    background("#d0e2ff")

def write_text():
    fill(PALETTE[-1])
    text_size(200)
    text_align(CENTER)
    text("Ï€", 80, 120)


def pi_circle():
    no_stroke()
    push_matrix()
    angulo = 3
    translate(WIDTH / 2, HEIGHT / 2)
    radius = -360
    d = 16
    for i in range(0, len(PI)):
        rotate(radians(angulo))
        char = PI[i]
        digit = 0 if char == "." else int(char)
        fill(PALETTE[digit])
        if i % 4 == 0 and radius < 0:
            radius = radius + 1
        if i % 100 == 0 and d > 1:
            d -= 1
        circle(0, radius, d)
    pop_matrix()


run_sketch()
